  class HeaderComponent {
    constructor(orderService, accountService // private activatedRoute: ActivatedRoute,
    // private productService: ProductService,
    ) {
      this.orderService = orderService;
      this.accountService = accountService;
      this.basket = [];
      this.total = 0;
      this.count = 0;
      this.isOpen = false;
      this.isLogin = true;
      this.loginUrl = '';
      this.loginPage = '';
      this.callUser = false;
      this.status = false;
    }

    ngOnInit() {
      this.loadBasket();
      this.updateBasket();
      this.checkUserLogin();
      this.checkUpdatesLogin();
    }

    loadBasket() {
      if (localStorage.length > 0 && localStorage.getItem('basket')) {
        this.basket = JSON.parse(localStorage.getItem('basket'));
      }

      this.getTotalPrice();
    }

    getTotalPrice() {
      this.total = this.basket.reduce((total, prod) => total + prod.count * prod.price, 0);
      this.count = this.basket.reduce((total, prod) => total + prod.count, 0);
    }

    updateBasket() {
      this.orderService.changeBasket.subscribe(() => {
        this.loadBasket();
      });
    }

    openModal() {
      this.isOpen = !this.isOpen;
    }

    closeModal(value) {
      if (value) {
        this.isOpen = false;
      } else {
        this.isOpen = !this.isOpen;
      }
    }

    productCount(product, value) {
      if (value) {
        ++product.count;
      } else if (!value && product.count > 1) {
        --product.count;
      }

      this.getTotalPrice();
    } //deleting item from basket 


    deleteFromBasket(product) {
      this.basket = JSON.parse(localStorage.getItem('basket'));

      if (this.basket.some(prod => prod.id === product.id)) {
        const index = this.basket.findIndex(prod => prod.id === product.id);
        this.basket.splice(index);
        localStorage.setItem('basket', JSON.stringify(this.basket));
        this.updateBasket();
        this.getTotalPrice();
      }
    } //show user ROLE in header


    checkUserLogin() {
      const currentUser = JSON.parse(localStorage.getItem('currentUser'));

      if (currentUser && currentUser.role === ROLE.ADMIN) {
        this.isLogin = true;
        this.loginUrl = 'admin';
        this.loginPage = 'Адмін';
      } else if (currentUser && currentUser.role === ROLE.USER) {
        this.isLogin = true;
        this.loginUrl = 'account';
        this.loginPage = 'Юзер';
      } else {
        this.isLogin = false;
        this.loginUrl = '';
        this.loginPage = '';
      }
    }

    checkUpdatesLogin() {
      this.accountService.isUserLogin$.subscribe(() => {
        this.checkUserLogin();
      });
    }

    callBtn() {
      this.callUser = !this.callUser;

      if (this.callUser) {
        this.visibile_block = 'display: block; opacity: 1; visibility: visible;';
        this.not_visibile_block = '';
        document.body.style.overflow = 'hidden';
        this.status = !this.status;
      } else {
        this.status = !this.status;
        this.visibile_block = '';
