  class HeaderComponent {
    constructor(orderService, accountService // private activatedRoute: ActivatedRoute,
    // private productService: ProductService,
    ) {
      this.orderService = orderService;
      this.accountService = accountService;
      this.basket = [];
      this.total = 0;
      this.count = 0;
      this.isOpen = false;
      this.isShow = false;
      this.isLogin = true;
      this.loginUrl = '';
      this.loginPage = '';
    }

    ngOnInit() {
      this.loadBasket();
      this.updateBasket();
      this.checkUserLogin();
      this.checkUpdatesUserLogin();
    }

    togle() {
      this.isShow = !this.isShow;
    }

    loadBasket() {
      if (localStorage.length > 0 && localStorage.getItem('basket')) {
        this.basket = JSON.parse(localStorage.getItem('basket'));
      }

      this.getTotalPrice();
    }

    getTotalPrice() {
      this.total = this.basket.reduce((total, prod) => total + prod.count * prod.price, 0);
      this.count = this.basket.reduce((total, prod) => total + prod.count, 0);
    }

    updateBasket() {
      this.orderService.changeBasket.subscribe(() => {
        this.loadBasket();
      });
    }

    openModal() {
      this.isOpen = !this.isOpen;
    }

    closeModal(value) {
      if (value) {
        this.isOpen = false;
      } else {
        this.isOpen = !this.isOpen;
      }
    }

    productCount(product, value) {
      if (value) {
        ++product.count;
      } else if (!value && product.count > 1) {
        --product.count;
      }

      this.getTotalPrice();
    } //by form add products? 
    //check if there is something in the basket add to local storage


    addToBasket(product) {
      let basket = [];

      if (localStorage.length > 0 && localStorage.getItem('basket')) {
        basket = JSON.parse(localStorage.getItem('basket'));

        if (basket.some(prod => prod.id === product.id)) {
          const index = basket.findIndex(prod => prod.id === product.id);
          basket[index].count += product.count;
        } else {
          basket.push(product);
        }
      } else {
        basket.push(product);
      }

      localStorage.setItem('basket', JSON.stringify(basket));
      product.count = 1;
      this.orderService.changeBasket.next(true);
      this.openModal();
    } //show user ROLE in header


    checkUserLogin() {
      const currentUser = JSON.parse(localStorage.getItem('currentUser'));

      if (currentUser && currentUser.role === ROLE.ADMIN) {
        this.isLogin = true;
        this.loginUrl = 'admin';
        this.loginPage = 'Admin';
      } else if (currentUser && currentUser.role === ROLE.USER) {
        this.isLogin = true;
        this.loginUrl = 'user-profile';
        this.loginPage = 'User';
      } else {
        this.isLogin = false;
        this.loginUrl = '';
        this.loginPage = '';
      }
    } //check login ROLE changes 


    checkUpdatesUserLogin() {
      this.accountService.isUserLogin$.subscribe(() => {
        this.checkUserLogin();
      });
